// Scatter Oiled Ballbearings direct macro (inlined).
// Place template allowing user prompt if not already placed by activity target.
if (!item) return;
let template = canvas.templates?.placeables.find(t => t.document.getFlag('oiled-ballbearings','pending') && t.document.parent?.uuid === item.parent?.uuid);
if (!template) {
  // Use item.placeTemplate if available (system helper) else manual creation.
  if (item.placeTemplate) {
    template = await item.placeTemplate();
  } else {
    const data = { t: 'rect', distance: 10, width: 10, user: game.user.id, flags: { 'oiled-ballbearings': { pending: true } } };
    const created = await canvas.scene.createEmbeddedDocuments('MeasuredTemplate', [data]);
    template = created[0];
  }
}
if (!template) { ui.notifications.warn('Template placement cancelled.'); return; }
// Update styling and metadata.
await template.update({
  borderColor: '#FF6400',
  fillColor: '#FF640080',
  text: 'DC 13 Dex Save',
  flags: { 'oiled-ballbearings': { dc: 13, saveAbility: 'dex', effect: 'DC 13 Dex save or fall prone', damageType: 'none', condition: 'prone on failure' } }
});
ui.notifications.info('Oiled Ballbearings scattered! 10x10 ft area (DC 13 Dex or prone).');
// Consume item if uses depleted after activity consumption.
if (item.system?.uses?.value !== undefined && item.system.uses.value <= 0 && item.system.uses.autoDestroy) {
  // Ensure not already deleted.
  try { await item.delete(); } catch(e) {}
}
