macro.command:
// Increase max HP by twice actor's level
const level = actor.system.details.level || 1;
const value = 2 * level;
await actor.createEmbeddedDocuments("ActiveEffect", [{
  label: "Adventurer's Stew: Max HP +2/level",
  icon: "icons/consumables/food/stew-bowl-brown.webp",
  changes: [{ key: 'system.attributes.hp.max', mode: 2, value: value, priority: 20 }],
  duration: { special: ["longRest"] },
  origin: item.uuid
}]);
ui.notifications.info("Max HP increased by " + value + " until your next rest.");
