macro.command:
// BLT: +1 to all rolls, +10ft speed, +10ft darkvision, 10 temp HP
await actor.createEmbeddedDocuments("ActiveEffect", [{
  label: "BLT Meal",
  icon: "icons/consumables/food/sandwich-bacon-lettuce-tomato.webp",
  changes: [
    { key: 'system.bonuses.abilities.check', mode: 2, value: '+1', priority: 20 },
    { key: 'system.bonuses.abilities.save', mode: 2, value: '+1', priority: 20 },
    { key: 'system.bonuses.abilities.attack', mode: 2, value: '+1', priority: 20 },
    { key: 'system.bonuses.abilities.damage', mode: 2, value: '+1', priority: 20 },
    { key: 'system.attributes.movement.walk', mode: 2, value: '+10', priority: 20 },
    { key: 'system.attributes.senses.darkvision', mode: 2, value: '+10', priority: 20 },
    { key: 'system.attributes.hp.temp', mode: 5, value: '10', priority: 20 }
  ],
  duration: { special: ["longRest"] },
  origin: item.uuid
}]);
ui.notifications.info("BLT meal effects applied until your next rest.");
