macro.command:
// Golden Crab Cake: Immunity to frightened and charmed
await actor.createEmbeddedDocuments("ActiveEffect", [{
  label: "Golden Crab Cake Meal",
  icon: "icons/consumables/food/crab-cake-golden.webp",
  changes: [
    { key: 'system.traits.ci.value', mode: 2, value: 'frightened', priority: 20 },
    { key: 'system.traits.ci.value', mode: 2, value: 'charmed', priority: 20 }
  ],
  duration: { special: ["longRest"] },
  origin: item.uuid
}]);
ui.notifications.info("Immunity to frightened and charmed until your next rest.");
