macro.command:
// Ironrock Candy: 18 AC, resistance to non-magical B/P/S
await actor.createEmbeddedDocuments("ActiveEffect", [{
  label: "Ironrock Candy Meal",
  icon: "icons/consumables/food/candy-rock-brown.webp",
  changes: [
    { key: 'system.attributes.ac.value', mode: 5, value: '18', priority: 20 },
    { key: 'system.traits.dr.value', mode: 2, value: 'bludgeoning', priority: 20 },
    { key: 'system.traits.dr.value', mode: 2, value: 'piercing', priority: 20 },
    { key: 'system.traits.dr.value', mode: 2, value: 'slashing', priority: 20 }
  ],
  duration: { special: ["longRest"] },
  origin: item.uuid
}]);
ui.notifications.info("AC set to 18 and resistance to non-magical B/P/S until your next rest.");
