macro.command:
// Ocean's Bounty: Swim speed = land speed, no underwater attack penalty
const landSpeed = actor.system.attributes.movement.walk || 30;
await actor.createEmbeddedDocuments("ActiveEffect", [{
  label: "Ocean's Bounty Meal",
  icon: "icons/consumables/food/fish-blue.webp",
  changes: [
    { key: 'system.attributes.movement.swim', mode: 5, value: landSpeed, priority: 20 }
  ],
  duration: { special: ["longRest"] },
  origin: item.uuid
}]);
ui.notifications.info("Swim speed set to land speed until your next rest.");
