macro.command:
// Roast Beast: STR is 30 for Strength ability checks
await actor.createEmbeddedDocuments("ActiveEffect", [{
  label: "Roast Beast Meal",
  icon: "icons/consumables/food/roast-beast-brown.webp",
  changes: [
    { key: 'system.abilities.str.value', mode: 5, value: '30', priority: 20 }
  ],
  duration: { special: ["longRest"] },
  origin: item.uuid
}]);
ui.notifications.info("Strength set to 30 for ability checks until your next rest.");
