macro.command:
// Swift Foot Stew: +15ft movement speed
await actor.createEmbeddedDocuments("ActiveEffect", [{
  label: "Swift Foot Stew Meal",
  icon: "icons/consumables/food/stew-bowl-brown.webp",
  changes: [
    { key: 'system.attributes.movement.walk', mode: 2, value: '+15', priority: 20 }
  ],
  duration: { special: ["longRest"] },
  origin: item.uuid
}]);
ui.notifications.info("+15ft movement speed until your next rest.");
