macro.command:
// Place Oiled Ballbearings Template
if (args[0] === "on") {
  // Use Foundry's built-in template placement system
  const template = await item.placeTemplate();

  if (template) {
    // Add custom flags and styling to the placed template
    await template.update({
      borderColor: "#FF6400",
      fillColor: "#FF640080",
      text: "DC 13 Dex Save",
      flags: {
        "oiled-ballbearings": {
          dc: 13,
          saveAbility: "dex",
          effect: "DC 13 Dex save or fall prone",
          damageType: "none",
          condition: "prone on failure"
        }
      }
    });

    ui.notifications.info("Oiled Ballbearings scattered! 10x10 foot area created (DC 13 Dex save or prone).");

    // Force delete the item after successful template placement
    await item.delete();
  }
}
