macro.command:
// Apply Harmony Tea Effect as activity macro.
const effectData = {
  name: 'Harmony Tea Effect',
  icon: 'icons/consumables/drinks/cup-simple-clay-brown.webp',
  changes: [
    { key: 'system.bonuses.abilities.con', mode: 2, value: '1', priority: 20 }
  ],
  flags: { dae: { specialDuration: ['longRest', 'shortRest'] } }
};
await actor.createEmbeddedDocuments('ActiveEffect', [effectData]);
ui.notifications.info('Harmony Tea effect applied! +1 to concentration checks until rest.');
