macro.command:
// Apply Lemon Salt Crackers Effect as activity macro.
const effectData = {
  name: 'Lemon Salt Crackers Effect',
  icon: 'icons/consumables/food/bread-loaf-brown-grey.webp',
  changes: [
    { key: 'system.bonuses.abilities.str', mode: 2, value: '1', priority: 20 },
    { key: 'system.bonuses.abilities.dex', mode: 2, value: '1', priority: 20 },
    { key: 'system.bonuses.abilities.con', mode: 2, value: '1', priority: 20 }
  ],
  flags: { dae: { specialDuration: ['longRest', 'shortRest'] } }
};
await actor.createEmbeddedDocuments('ActiveEffect', [effectData]);
ui.notifications.info('Lemon Salt Crackers effect applied! +1 to STR, DEX, CON checks until rest.');
