macro.command:
// Apply Moonspring Skittles Effect as activity macro.
const effectData = {
  name: 'Moonspring Skittles Effect',
  icon: 'icons/consumables/candy/candy-wrapped-blue.webp',
  changes: [
    { key: 'system.bonuses.abilities.int', mode: 2, value: '1', priority: 20 },
    { key: 'system.bonuses.abilities.wis', mode: 2, value: '1', priority: 20 },
    { key: 'system.bonuses.abilities.cha', mode: 2, value: '1', priority: 20 }
  ],
  flags: { dae: { specialDuration: ['longRest', 'shortRest'] } }
};
await actor.createEmbeddedDocuments('ActiveEffect', [effectData]);
ui.notifications.info('Moonspring Skittles effect applied! +1 to INT, WIS, CHA checks until rest.');
